import React, { useState } from 'react';
import { Search, X, Clock, Activity, Check, XCircle, Eye, Edit } from 'lucide-react';
import AppleDisp from './AppleDisp';
import './AdminDashboard.css';

const AdminDashboard = ({ isAdmin }) => {
  const [activeTab, setActiveTab] = useState('pending'); // pending, current, apples
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedAdmin, setSelectedAdmin] = useState(null);
  const [showActivityLog, setShowActivityLog] = useState(false);
  const [selectedApple, setSelectedApple] = useState(null);
  const [showAppleDisp, setShowAppleDisp] = useState(false);
  const [isEditing, setIsEditing] = useState(false);

  // Mock data - Pending admin requests
  const [pendingAdmins, setPendingAdmins] = useState([
    {
      id: 1,
      name: 'John Smith',
      email: 'john.smith@email.com',
      requestDate: '2025-10-20',
      reason: 'Research team member - Apple genetics study',
      status: 'pending'
    },
    {
      id: 2,
      name: 'Emily Chen',
      email: 'emily.chen@email.com',
      requestDate: '2025-10-22',
      reason: 'Database administrator for inventory management',
      status: 'pending'
    },
    {
      id: 3,
      name: 'Michael Brown',
      email: 'm.brown@email.com',
      requestDate: '2025-10-23',
      reason: 'Field researcher - Ontario apple varieties',
      status: 'pending'
    }
  ]);

  // Mock data - Current admins
  const [currentAdmins, setCurrentAdmins] = useState([
    {
      id: 101,
      name: 'Namratha Muraleedharan',
      email: 'namratha@appleverse.com',
      role: 'Super Admin',
      isActive: true,
      joinDate: '2024-01-15',
      lastLogin: '2025-10-25 09:30 AM',
      activityLog: [
        { action: 'Logged in', timestamp: '2025-10-25 09:30 AM', details: 'IP: 192.168.1.1' },
        { action: 'Updated apple variety', timestamp: '2025-10-25 10:15 AM', details: 'Modified Honeycrisp data' },
        { action: 'Approved admin request', timestamp: '2025-10-24 02:45 PM', details: 'Approved Sarah Johnson' },
        { action: 'Created template', timestamp: '2025-10-24 11:20 AM', details: 'New inventory template' }
      ]
    },
    {
      id: 102,
      name: 'Sana Sehgal',
      email: 'sana@appleverse.com',
      role: 'Admin',
      isActive: true,
      joinDate: '2024-03-10',
      lastLogin: '2025-10-24 03:45 PM',
      activityLog: [
        { action: 'Logged in', timestamp: '2025-10-24 03:45 PM', details: 'IP: 192.168.1.5' },
        { action: 'Added new apple variety', timestamp: '2025-10-24 04:00 PM', details: 'Added Ambrosia variety' },
        { action: 'Updated contact info', timestamp: '2025-10-23 10:30 AM', details: 'Changed phone number' }
      ]
    },
    {
      id: 103,
      name: 'Jayanth Kumar',
      email: 'jayanth@appleverse.com',
      role: 'Admin',
      isActive: false,
      joinDate: '2024-06-20',
      lastLogin: '2025-10-20 11:00 AM',
      activityLog: [
        { action: 'Logged in', timestamp: '2025-10-20 11:00 AM', details: 'IP: 192.168.1.8' },
        { action: 'Exported data', timestamp: '2025-10-20 11:15 AM', details: 'Downloaded apple inventory CSV' }
      ]
    },
    {
      id: 104,
      name: 'Saima Khatoon',
      email: 'saima@appleverse.com',
      role: 'Admin',
      isActive: true,
      joinDate: '2024-08-05',
      lastLogin: '2025-10-25 08:00 AM',
      activityLog: [
        { action: 'Logged in', timestamp: '2025-10-25 08:00 AM', details: 'IP: 192.168.1.12' },
        { action: 'Updated apple images', timestamp: '2025-10-25 08:30 AM', details: 'Uploaded 5 new images for Gala' },
        { action: 'Rejected admin request', timestamp: '2025-10-24 01:20 PM', details: 'Rejected Mark Wilson' }
      ]
    }
  ]);

  // Mock data - Apple varieties with full details
  const [apples, setApples] = useState([
    {
      id: 1,
      name: 'Honeycrisp',
      origin: 'USA',
      status: 'Active',
      lastUpdated: '2025-10-20',
      // Full apple data
      acno: 'A-2024-001',
      accession: 'ACC-HNY-001',
      cultivar_name: 'Honeycrisp',
      e_origin_country: 'United States',
      e_origin_province: 'Minnesota',
      e_origin_city: 'Minneapolis',
      e_genus: 'Malus',
      e_species: 'domestica',
      e_pedigree: 'Keepsake × MN1627',
      e_breeder: 'David Bedford & Jim Luby',
      e_collector: 'University of Minnesota',
      description: 'Exceptionally crisp and juicy with a perfect balance of sweetness and tartness.',
      taste: 'Sweet-Tart',
      texture: 'Extra Crisp',
      uses: 'Fresh Eating, Salads, Desserts',
      harvestSeason: 'Late September',
      hardiness: 'Zone 3-6',
      storage: '4-6 months'
    },
    {
      id: 2,
      name: 'Gala',
      origin: 'New Zealand',
      status: 'Active',
      lastUpdated: '2025-10-18',
      acno: 'A-2024-002',
      accession: 'ACC-GAL-001',
      cultivar_name: 'Gala',
      e_origin_country: 'New Zealand',
      e_origin_province: 'Hawke\'s Bay',
      e_origin_city: 'Havelock North',
      e_genus: 'Malus',
      e_species: 'domestica',
      e_pedigree: 'Kidd\'s Orange Red × Golden Delicious',
      e_breeder: 'J.H. Kidd',
      e_collector: 'New Zealand Research',
      description: 'Mild, sweet, and aromatic with a thin skin.',
      taste: 'Sweet',
      texture: 'Crisp',
      uses: 'Fresh Eating, Salads, Sauces',
      harvestSeason: 'Mid August',
      hardiness: 'Zone 5-8',
      storage: '3-4 months'
    },
    {
      id: 3,
      name: 'Fuji',
      origin: 'Japan',
      status: 'Active',
      lastUpdated: '2025-10-15',
      acno: 'A-2024-003',
      accession: 'ACC-FUJ-001',
      cultivar_name: 'Fuji',
      e_origin_country: 'Japan',
      e_origin_province: 'Aomori',
      e_origin_city: 'Fujisaki',
      e_genus: 'Malus',
      e_species: 'domestica',
      e_pedigree: 'Ralls Janet × Red Delicious',
      e_breeder: 'Tohoku Research Station',
      e_collector: 'Japanese Agricultural Research',
      description: 'Extremely sweet and crisp with dense flesh.',
      taste: 'Very Sweet',
      texture: 'Crisp',
      uses: 'Fresh Eating, Salads, Desserts',
      harvestSeason: 'October',
      hardiness: 'Zone 6-9',
      storage: '5-7 months'
    },
    {
      id: 4,
      name: 'Granny Smith',
      origin: 'Australia',
      status: 'Active',
      lastUpdated: '2025-10-22',
      acno: 'A-2024-004',
      accession: 'ACC-GRN-001',
      cultivar_name: 'Granny Smith',
      e_origin_country: 'Australia',
      e_origin_province: 'New South Wales',
      e_origin_city: 'Sydney',
      e_genus: 'Malus',
      e_species: 'domestica',
      e_pedigree: 'Chance seedling',
      e_breeder: 'Maria Ann Smith',
      e_collector: 'Australian Cultivar Collection',
      description: 'Bright green with a very tart flavor.',
      taste: 'Tart',
      texture: 'Firm',
      uses: 'Baking, Cooking, Fresh Eating',
      harvestSeason: 'October',
      hardiness: 'Zone 6-9',
      storage: '5-7 months'
    }
  ]);

  // Handle approve request
  const handleApprove = (adminId) => {
    setPendingAdmins(prev => prev.filter(admin => admin.id !== adminId));
    alert('Admin request approved!');
  };

  // Handle reject request
  const handleReject = (adminId) => {
    setPendingAdmins(prev => prev.filter(admin => admin.id !== adminId));
    alert('Admin request rejected!');
  };

  // Handle toggle active status
  const toggleActiveStatus = (adminId) => {
    setCurrentAdmins(prev => prev.map(admin => 
      admin.id === adminId ? { ...admin, isActive: !admin.isActive } : admin
    ));
  };

  // Handle view activity log
  const viewActivityLog = (admin) => {
    setSelectedAdmin(admin);
    setShowActivityLog(true);
  };

  // Handle view apple details
  const handleViewApple = (apple) => {
    setSelectedApple(apple);
    setIsEditing(false);
    setShowAppleDisp(true);
  };

  // Handle edit apple
  const handleEditApple = (apple) => {
    setSelectedApple(apple);
    setIsEditing(true);
    setShowAppleDisp(true);
  };

  // Handle save apple changes
  const handleSaveApple = (updatedApple) => {
    setApples(prev => prev.map(apple => 
      apple.id === updatedApple.id ? updatedApple : apple
    ));
    setShowAppleDisp(false);
    alert('Apple variety updated successfully!');
  };

  // Filter admins based on search
  const filteredPendingAdmins = pendingAdmins.filter(admin =>
    admin.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
    admin.email.toLowerCase().includes(searchQuery.toLowerCase())
  );

  const filteredCurrentAdmins = currentAdmins.filter(admin =>
    admin.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
    admin.email.toLowerCase().includes(searchQuery.toLowerCase())
  );

  return (
    <div className="admin-dashboard">
      <div className="admin-header">
        <h1>🔐 Admin Dashboard</h1>
        <p className="admin-subtitle">Manage administrators and system access</p>
      </div>

      {/* Tabs */}
      <div className="admin-tabs">
        <button 
          className={`admin-tab ${activeTab === 'pending' ? 'active' : ''}`}
          onClick={() => setActiveTab('pending')}
        >
          Pending Requests
          {pendingAdmins.length > 0 && (
            <span className="badge">{pendingAdmins.length}</span>
          )}
        </button>
        <button 
          className={`admin-tab ${activeTab === 'current' ? 'active' : ''}`}
          onClick={() => setActiveTab('current')}
        >
          Current Admins
          <span className="badge-secondary">{currentAdmins.length}</span>
        </button>
        <button 
          className={`admin-tab ${activeTab === 'apples' ? 'active' : ''}`}
          onClick={() => setActiveTab('apples')}
        >
          Apple Varieties
          <span className="badge-secondary">{apples.length}</span>
        </button>
      </div>

      {/* Search Bar */}
      {activeTab !== 'apples' && (
        <div className="search-bar">
          <Search size={20} />
          <input
            type="text"
            placeholder="Search by name or email..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
          />
          {searchQuery && (
            <button className="clear-search" onClick={() => setSearchQuery('')}>
              <X size={18} />
            </button>
          )}
        </div>
      )}

      {/* Content Area */}
      <div className="admin-content">
        {/* PENDING REQUESTS TAB */}
        {activeTab === 'pending' && (
          <div className="pending-requests">
            <h2>Pending Admin Requests</h2>
            {filteredPendingAdmins.length === 0 ? (
              <div className="empty-state">
                <p>No pending requests at this time</p>
              </div>
            ) : (
              <div className="requests-list">
                {filteredPendingAdmins.map(admin => (
                  <div key={admin.id} className="request-card">
                    <div className="request-info">
                      <div className="request-header">
                        <h3>{admin.name}</h3>
                        <span className="request-date">
                          <Clock size={16} />
                          {admin.requestDate}
                        </span>
                      </div>
                      <p className="request-email">{admin.email}</p>
                      <p className="request-reason"><strong>Reason:</strong> {admin.reason}</p>
                    </div>
                    <div className="request-actions">
                      <button 
                        className="approve-btn"
                        onClick={() => handleApprove(admin.id)}
                      >
                        <Check size={18} />
                        Approve
                      </button>
                      <button 
                        className="reject-btn"
                        onClick={() => handleReject(admin.id)}
                      >
                        <XCircle size={18} />
                        Reject
                      </button>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>
        )}

        {/* CURRENT ADMINS TAB */}
        {activeTab === 'current' && (
          <div className="current-admins">
            <h2>Current Administrators</h2>
            <div className="admins-grid">
              {filteredCurrentAdmins.map(admin => (
                <div key={admin.id} className="admin-card">
                  <div className="admin-card-header">
                    <div>
                      <h3>{admin.name}</h3>
                      <span className={`role-badge ${admin.role.replace(' ', '-').toLowerCase()}`}>
                        {admin.role}
                      </span>
                    </div>
                    <div className="status-toggle">
                      <span className={`status-label ${admin.isActive ? 'active' : 'inactive'}`}>
                        {admin.isActive ? 'Active' : 'Inactive'}
                      </span>
                      <label className="toggle-switch">
                        <input
                          type="checkbox"
                          checked={admin.isActive}
                          onChange={() => toggleActiveStatus(admin.id)}
                        />
                        <span className="toggle-slider"></span>
                      </label>
                    </div>
                  </div>
                  
                  <div className="admin-details">
                    <p><strong>Email:</strong> {admin.email}</p>
                    <p><strong>Joined:</strong> {admin.joinDate}</p>
                    <p><strong>Last Login:</strong> {admin.lastLogin}</p>
                  </div>

                  <button 
                    className="view-activity-btn"
                    onClick={() => viewActivityLog(admin)}
                  >
                    <Eye size={18} />
                    View Activity Log
                  </button>
                </div>
              ))}
            </div>
          </div>
        )}

        {/* APPLE VARIETIES TAB */}
        {activeTab === 'apples' && (
          <div className="apples-list">
            <h2>Apple Varieties Database</h2>
            <table className="apples-table">
              <thead>
                <tr>
                  <th>ID</th>
                  <th>Variety Name</th>
                  <th>Origin</th>
                  <th>Status</th>
                  <th>Last Updated</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {apples.map(apple => (
                  <tr key={apple.id}>
                    <td>{apple.id}</td>
                    <td><strong>{apple.name}</strong></td>
                    <td>{apple.origin}</td>
                    <td>
                      <span className="status-badge active">{apple.status}</span>
                    </td>
                    <td>{apple.lastUpdated}</td>
                    <td>
                      <button 
                        className="action-btn"
                        onClick={() => handleViewApple(apple)}
                      >
                        <Eye size={14} />
                        View
                      </button>
                      {isAdmin && (
                        <button 
                          className="action-btn edit-btn"
                          onClick={() => handleEditApple(apple)}
                        >
                          <Edit size={14} />
                          Edit
                        </button>
                      )}
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>

      {/* Activity Log Modal */}
      {showActivityLog && selectedAdmin && (
        <div className="modal-overlay" onClick={() => setShowActivityLog(false)}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <div className="modal-header">
              <div>
                <h2>{selectedAdmin.name}</h2>
                <p className="modal-subtitle">{selectedAdmin.email}</p>
              </div>
              <button 
                className="modal-close"
                onClick={() => setShowActivityLog(false)}
              >
                <X size={24} />
              </button>
            </div>

            <div className="activity-log">
              <h3>
                <Activity size={20} />
                Recent Activity
              </h3>
              <div className="activity-timeline">
                {selectedAdmin.activityLog.map((log, index) => (
                  <div key={index} className="activity-item">
                    <div className="activity-dot"></div>
                    <div className="activity-content">
                      <p className="activity-action">{log.action}</p>
                      <p className="activity-details">{log.details}</p>
                      <p className="activity-time">
                        <Clock size={14} />
                        {log.timestamp}
                      </p>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Apple Display Modal */}
      {showAppleDisp && selectedApple && (
        <AppleDisp 
          appleData={selectedApple}
          onClose={() => setShowAppleDisp(false)}
          isEditing={isEditing}
          isAdmin={isAdmin}
          onSave={handleSaveApple}
        />
      )}
    </div>
  );
};

export default AdminDashboard;
