import React, { useState } from 'react';
import './createApple.css'; // optional, create for styling

export default function CreateApple() {
  const [step, setStep] = useState(0);
  const [uploadMethod, setUploadMethod] = useState(null); // 'single' or 'template'
  const [selectedFiles, setSelectedFiles] = useState([]);
  const [uploadedFiles, setUploadedFiles] = useState([]);
  const [uploadComplete, setUploadComplete] = useState(false);

  // Handle file selection
  const handleFileChange = (e) => {
    setSelectedFiles([...e.target.files]);
  };

  // Simulate upload
  const handleUpload = () => {
    if (selectedFiles.length === 0) return;
    setUploadedFiles((prev) => [...prev, ...selectedFiles]);
    setUploadComplete(true);
    setSelectedFiles([]);
  };

  // Restart the process
  const handleRestart = () => {
    setStep(0);
    setUploadMethod(null);
    setSelectedFiles([]);
    setUploadedFiles([]);
    setUploadComplete(false);
  };

  return (
    <div className="create-apple-container" style={{ textAlign: 'center', marginTop: '50px' }}>
      <h1>Create New Apple Resource 🍎</h1>

      {/* Timeline */}
      <div className="timeline-bar">
        {[0, 1, 2, 3].map((s) => (
          <span key={s} className={`timeline-step ${step === s ? 'active' : ''}`} style={{ margin: '0 10px', fontWeight: step === s ? 'bold' : 'normal' }}>
            Step {s}
          </span>
        ))}
      </div>

      {/* Step 0: Choose Upload Method */}
      {step === 0 && (
        <div className="step-content">
          <h2>Step 0: Start</h2>
          <p>Please choose your upload option:</p>
          <button onClick={() => { setUploadMethod('single'); setStep(1); }} style={{ marginRight: '10px' }}>
            Single File Upload
          </button>
          <button onClick={() => alert('Downloading template...')}>
            Download Template
          </button>
        </div>
      )}

      {/* Step 1: Upload */}
      {step === 1 && (
        <div className="step-content">
          <h2>Step 1: Upload</h2>
          {uploadMethod === 'single' && (
            <>
              <p>Select a file to upload:</p>
              <input type="file" onChange={handleFileChange} />
              {selectedFiles.length > 0 && (
                <ul>
                  {selectedFiles.map((file, idx) => <li key={idx}>{file.name}</li>)}
                </ul>
              )}
              <button onClick={handleUpload} style={{ marginTop: '10px' }}>Upload</button>
            </>
          )}
          {uploadMethod === 'template' && (
            <>
              <p>Use your downloaded template to prepare your file, then upload here:</p>
              <input type="file" onChange={handleFileChange} />
              {selectedFiles.length > 0 && (
                <ul>
                  {selectedFiles.map((file, idx) => <li key={idx}>{file.name}</li>)}
                </ul>
              )}
              <button onClick={handleUpload} style={{ marginTop: '10px' }}>Upload</button>
            </>
          )}
          <div style={{ marginTop: '20px' }}>
            <button onClick={() => setStep(0)} style={{ marginRight: '10px' }}>Back</button>
            {uploadComplete && <button onClick={() => setStep(2)}>Next</button>}
          </div>
        </div>
      )}

      {/* Step 2: Confirmation */}
      {step === 2 && (
        <div className="step-content">
          <h2>Step 2: Confirmation</h2>
          <p>Your file(s) have been uploaded successfully!</p>
          <ul>
            {uploadedFiles.map((file, idx) => <li key={idx}>{file.name}</li>)}
          </ul>
          <div style={{ marginTop: '20px' }}>
            <button onClick={() => setStep(1)} style={{ marginRight: '10px' }}>Back</button>
            <button onClick={() => setStep(3)}>Next</button>
          </div>
        </div>
      )}

      {/* Step 3: Review & Submit */}
      {step === 3 && (
        <div className="step-content">
          <h2>Step 3: Review & Submit</h2>
          <p>Uploaded files:</p>
          <ul>
            {uploadedFiles.map((file, idx) => <li key={idx}>{file.name}</li>)}
          </ul>
          <div style={{ marginTop: '20px' }}>
            <button onClick={handleRestart} style={{ marginRight: '10px' }}>Restart Process</button>
            <button onClick={() => alert('Resources submitted successfully! ✅')}>Final Submit</button>
          </div>
        </div>
      )}
    </div>
  );
}
