import React, { useState } from 'react';
import './createApple.css';

export default function CreateApple() {
  const [step, setStep] = useState(0);
  const [uploadMethod, setUploadMethod] = useState(null);
  const [selectedFiles, setSelectedFiles] = useState([]);
  const [uploadedFiles, setUploadedFiles] = useState([]);
  const [uploadComplete, setUploadComplete] = useState(false);

  const handleFileChange = (e) => setSelectedFiles([...e.target.files]);

  const handleUpload = () => {
    if (selectedFiles.length === 0) return;
    setUploadedFiles((prev) => [...prev, ...selectedFiles]);
    setUploadComplete(true);
    setSelectedFiles([]);
  };

  const handleRestart = () => {
    setStep(0);
    setUploadMethod(null);
    setSelectedFiles([]);
    setUploadedFiles([]);
    setUploadComplete(false);
  };

  return (
    <div className="create-apple-container">
      <h1 className="page-title">Create New Apple Resource 🍎</h1>

      {/* Timeline */}
      <div className="timeline">
        {['Start', 'Upload', 'Confirmation', 'Review'].map((label, idx) => (
          <div key={idx} className={`timeline-step ${step === idx ? 'active' : ''}`}>
            <div className="circle">{idx + 1}</div>
            <span>{label}</span>
          </div>
        ))}
      </div>

      {/* Step Content */}
      <div className="step-content-card">
        {step === 0 && (
          <div className="step">
            <h2>Step 0: Start</h2>
            <div className="instructions">
              <h4>Instructions:</h4>
              <ul>
                <li><strong>Single Upload:</strong> Quickly upload one file at a time.</li>
                <li><strong>Multiple Upload:</strong> Upload several files together for batch processing.</li>
                <li><strong>Download Template:</strong> Use it to format and prepare data for upload.</li>
              </ul>
            </div>
            <p>Please choose your upload option:</p>
            <div className="step0-options">
              <button
                className="btn-primary"
                onClick={() => {
                  setUploadMethod('single');
                  setStep(1);
                }}
              >
                Single File Upload
              </button>

              <button
                className="btn-primary"
                onClick={() => {
                  setUploadMethod('multi');
                  setStep(1);
                }}
              >
                Multiple File Upload
              </button>

              <button className="btn-secondary" onClick={() => alert('Downloading template...')}>
                Download Template
              </button>
            </div>
          </div>
        )}

        {step === 1 && (
          <div className="step">
            <h2>Step 1: Upload</h2>
            <p>Select file(s) to upload:</p>
            <input type="file" multiple={uploadMethod === 'multi'} onChange={handleFileChange} />
            {selectedFiles.length > 0 && (
              <ul>
                {selectedFiles.map((file, idx) => (
                  <li key={idx}>{file.name}</li>
                ))}
              </ul>
            )}
            <button className="btn-primary" onClick={handleUpload}>
              Upload
            </button>

            <div className="navigation-buttons">
              <button className="btn-secondary" onClick={() => setStep(0)}>
                Back
              </button>
              {uploadComplete && (
                <button className="btn-primary" onClick={() => setStep(2)}>
                  Next
                </button>
              )}
            </div>
          </div>
        )}

        {step === 2 && (
          <div className="step">
            <h2>Step 2: Confirmation</h2>
            <p>Your file(s) have been uploaded successfully!</p>
            <ul>
              {uploadedFiles.map((file, idx) => (
                <li key={idx}>{file.name}</li>
              ))}
            </ul>
            <div className="navigation-buttons">
              <button className="btn-secondary" onClick={() => setStep(1)}>
                Back
              </button>
              <button className="btn-primary" onClick={() => setStep(3)}>
                Next
              </button>
            </div>
          </div>
        )}

        {step === 3 && (
          <div className="step">
            <h2>Step 3: Review & Submit</h2>
            <p>Uploaded files:</p>
            <ul>
              {uploadedFiles.map((file, idx) => (
                <li key={idx}>{file.name}</li>
              ))}
            </ul>
            <div className="navigation-buttons">
              <button className="btn-secondary" onClick={handleRestart}>
                Restart
              </button>
              <button
                className="btn-primary"
                onClick={() => alert('Resources submitted successfully! ✅')}
              >
                Final Submit
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}
